var site="http://127.0.0.1:8000/api";function deleteRow(e){var row;e.parentNode.parentNode.remove()}document.getElementById("serviceButton").onclick=function(){var xhr=new XMLHttpRequest,url=site+"/permission";xhr.open("get",url,!0),xhr.setRequestHeader("Content-Type","application/json"),xhr.onreadystatechange=function(){if(4===xhr.readyState&&200===xhr.status){var json=JSON.parse(xhr.responseText);let content="";content+='\n                    <tr>\n                    <td>\n                        <select class="form-select" name="permissions[]" aria-label="Default select example">\n                        <option selected>Open this permission menu</option>\n                        ';for(let object in json)content+='<option value="'+json[object].id+'">'+json[object].nameScreen+"</option>";content+='\n                </select>\n                </td>\n                <td>\n                <select class="form-select" name="roles[]" aria-label="Default select example">\n                  <option selected>Open this role menu</option>\n                  <option value="all" class="text-capitalize">all</option>\n                  <option value="write" class="text-capitalize">write</option>\n                  <option value="read" class="text-capitalize">read</option>\n                  <option value="edit" class="text-capitalize">edit</option>\n                  <option value="delete" class="text-capitalize">delete</option>\n                </select>   \n              </td>\n              <td>\n                  <button class="btn btn-danger" type="button" onclick="deleteRow(this);">Remove</button>\n              </td>\n            </tr>\n  ',document.getElementById("tbody").insertAdjacentHTML("afterend",content)}},xhr.send()};